// https://en.wikipedia.org/wiki/Linear_congruential_generator
uint LCGHashI(in uint seed) {
    return (seed * 16807u) % 2147483647u;
}
float LCGRandf1() {
    randomState = LCGHashI(randomState);
    return float(randomState & 0xffffffu) * invMax24Bit;
}

// simple xor shift random number generator https://en.wikipedia.org/wiki/Xorshift
uint XorShiftHash32(in uint seed) {
    seed ^= seed << 13;
    seed ^= seed >> 17;
    seed ^= seed << 5;
    // xorshift* - Multiplying by a prime makes it better, but still not great. (fails 0 seed)
    return seed;// * 2156034509u;
}
float XORRandf1() {
    randomState = XorShiftHash32(randomState);
    return float(randomState & 0xffffffu) * invMax24Bit;
}

// https://en.wikipedia.org/wiki/Permuted_congruential_generator
// RXS-M-XS algorithm
uint PCGHashI(in uint seed) {
    uint count=uint(seed >> 28);
    seed ^= seed >> (4u + count);
    seed *= 277803737u;
    return seed ^ (seed >> 22);
}
float PCGRandf1() {
    randomState = PCGHashI(randomState);
    return float(randomState & 0xffffffu) * invMax24Bit;
}

