bool HashedSeedButton(vec2 iMouse, vec2 iResolution) {
    return (iMouse.x / iResolution.x > 0.55) && (iMouse.y > iResolution.y *0.92);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    bool hashedSeed = HashedSeedButton(iMouse.xy, iResolution.xy);

    vec2 uv = fragCoord.xy / iResolution.xy;
	vec4 backpixel = texture(iChannel0, uv);
    // If we use the mouse to change the view, reset the pixel accumulation buffer
    if (iMouse.z > 0.0) backpixel = vec4(0.0);

    SetRandomSeed(fragCoord, iResolution.xy, iFrame, hashedSeed);
    float col = 0.0;

    // Make 3 vertical zones for visualizing different things.
    int zoney = int(floor(uv.y / spacing.y * 0.25));
    vec2 zoneFract = fract(uv / spacing * vec2(1.0,0.25)) * spacing * iResolution.xy / vec2(1.0,0.25);

    int reps = 1000;
    if (zoney == 0) reps = 1;
    if (zoney == 1) {
        reps = min(reps, int(zoneFract.x)+1);
        if (!hashedSeed) {
            randomState = uint(fragCoord.y) + uint(iFrame);
            //if (iFrame < 60) randomState = 0u;  // Test zero-state recovery
        }
    }
    if (zoney == 3) {
        reps = min(reps, int(zoneFract.x)+1);
        randomState = 0u;
    }
    for (int i = 0; i < reps; i++) {
        if (zoney != 2) col = 0.0;

#ifndef BIT_BY_BIT_DEBUG
        // Visualize all the different PRNG algos
        if (uv.x < spacing.x*2.0) col += RandFloat();
        else if (uv.x < spacing.x*3.0) col += RandVec2().x;
        else if (uv.x < spacing.x*4.0) col += RandVec2().y;
        else if (uv.x < spacing.x*5.0) col += RandVec3().x;
        else if (uv.x < spacing.x*6.0) col += RandVec3().y;
        else if (uv.x < spacing.x*7.0) col += RandVec3().z;
        else if (uv.x < spacing.x*8.0) col += XORRandf1();
        else if (uv.x < spacing.x*9.0) col += rand1iq();
        else if (uv.x < spacing.x*10.0) col += LCGRandf1();
#else
        // Visualize each bit of the state independently.
        int bit = int(floor(uv.x / spacing.x));
        col += RandShifted(8 - bit);
#endif
    }
    if (zoney == 2) col /= float(reps);
    col = saturate(col);

	fragColor = vec4(vec3(saturate(col)),1.0);
    if (zoney == 2) fragColor += backpixel;
}
