void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord/iResolution.xy;
    vec4 col = vec4(0.0);
    for(int i=-1; i<2; i++){
        for(int j=-1; j<2; j++){
            vec4 add = texture(iChannel0,uv+vec2(float(i)/iResolution.x,float(j)/iResolution.y));
            float div = 4.0;
			if(abs(i)>0||abs(j)>0){
                div = 2.0;
                if(abs(i)>0&&abs(j)>0){
                    div = 1.0;
                }
            }
            col += add*div;
        }
    }
    fragColor = col/16.0;
}

const float pi = 3.14159265358979323846264338327950288;
const float e = 2.71828182845904523536028747135266250;
const float l2 = log(2.);

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 fgoff = vec2(fract((fragCoord.x+iTime*pi)*9542.385482758), fract((fragCoord.y+iTime*e)*2594.29358952));
    fragCoord+=fgoff;
    vec2 uv = 4.0*(fragCoord-iResolution.xy/2.0)/iResolution.x;
    int i=0;
    vec2 p = uv;
    vec2 n0 = vec2(sin(iTime/e)*0.7,cos(iTime/pi)*0.7);
    vec2 sq = p*p;
    while(i<1005 && sq.x+sq.y<16.){
        p = vec2(sq.x-sq.y+n0.x, 2.*p.x*p.y+n0.y);
        sq = p*p;
        i++;
    }
    float m2 = sq.x+sq.y;
    float iter = float(i)-log(log(m2)/l2)/l2;
    float a = clamp((1.0-cos(iter/5.0))/2.0,0.,1.);
    vec3 col = vec3(sin(a*5.0)+1.0,sin(a*5.0+1.)+1.0,sin(a*5.0+2.)+1.0)*a*1.;
    fragCoord-=fgoff;
    uv = fragCoord/iResolution.xy;
    fragColor = (vec4(col,1.0)+texture(iChannel0,uv))/2.;
}
