// analytic version of https://shadertoy.com/view/ttBczh
// variant of https://shadertoy.com/view/wtSczh
// variant of https://shadertoy.com/view/3lScRR
// variant of https://shadertoy.com/view/ttlcDs


void mainImage(out vec4 O, vec2 u) {
    vec2 R = iResolution.xy*.1, V = u/R.y,  C,
    U = V*5. + V.y* cos( iTime + V.x*.56 + 11.*O.xw );
    float d, o = 0., t = o, v;
    for (int k=0; k<49; k++)
        C = U - round((U+vec2(k%7-3,k/7-3))*.2) / .2 ,
        V = C * inverse(transpose(mat2(dFdx(U),dFdy(U)) ))/R.y,
        d = max(0.,1.-.05*dot(C,C)), d*=d*d*d,
        v = length(V), V *= 36./v * cos(36.*v +(U-.2*C).x) ,
        o += ( .4 + dot( V , cos(2.*iTime+vec2(0,11)) ) ) * d,
        t += d*d;
    t = ( .5 + .003*o/sqrt(t) )* smoothstep(1.,-1.,U.y -37.);
    O += sqrt( t );
}
