vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

float rand(vec2 co){ return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453); }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float res = 300.0;
    vec2 uv = round(fragCoord/iResolution.xy * res) / res;

    vec4 m = iMouse / iResolution.xxxx;

    // Time varying pixel color
    // vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float normal = rand(uv) * 3.141592;
    float source = (m.x + m.y) * 3.141592;

    float brightness = cos(normal - source);

    float v = 0.5*rand(uv) + 0.5*brightness;

    vec3 col = vec3(5.0 / 6.0, 1.0, clamp(v, 0.5, 1.0));


    // Output to screen
    fragColor = vec4(hsv2rgb(col),1.0);
}
