const vec3 layer1Color = vec3(0.0, 0.0, 254.0/255.0);
const vec3 layer2Color = vec3(57.0/255.0, 73.0/255.0, 251.0/255.0);
const vec3 layer3Color = vec3(0.0, 0.0, 4.0/255.0);

#define scale 30.0
#define offset 10.0

#define fisheye_amount -0.1

float hash(vec3 p3)
{
    p3.xy *= 400.0;
	p3  = fract(p3 * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    // fisheye

    uv -= 0.5;
    uv *= 1.0 - fisheye_amount / 2.0;

    float r = sqrt(dot(uv,uv));
    uv *= 1.0 + r * fisheye_amount;
    uv += 0.5;

    vec2 res = iResolution.xy;
    uv = clamp( uv, 1.0 / res, (res - 1.0) / res );

    // fisheye end

    uv.x *= iResolution.x / iResolution.y;

    vec3 col = layer1Color;

    float hash1 = round(hash(vec3(round(uv * scale) / scale, round(iTime * 15.0))));
    uv += offset / iResolution.xy;
    float hash2 = round(hash(vec3(round(uv * scale) / scale, round(iTime * 15.0 + 90.0))));

    col = mix(col, layer2Color, hash1);
    col = mix(col, layer3Color, hash2);

    fragColor = vec4(col,1.0);
}
