float SmoothMax( float a , float b , float k) {
	float h = clamp( ( b - a ) / k + 0.5, 0.0 , 1.0 );

    ///float smax = a * h + ( 1.0 - h ) * b - h * 0.5; 

    float smax = mix( a , b , h ) + h * ( 1.0 - h ) * k * 0.5;

    return smax;
}


float ShapeHeart( vec2 uv , float radius , float blur ) {


    uv.y = uv.y - SmoothMax( sqrt( abs( uv.x ) ) * 0.5 , blur, 0.1);


    float c;

    float d = length(uv);
    c = smoothstep( radius + blur , radius - blur , d );

    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5f * iResolution.xy ) /iResolution.y;
	vec2 m = iMouse.xy / iResolution.xy;

    vec3 col = vec3(0);

    float radius = 0.25;
    float blur = radius * m.y;

    uv.y *= 1.25;
    uv.y += 0.1;
    uv.x *= 0.7;


    float c = ShapeHeart( uv , radius , blur );

    col = vec3( 1.0 , 0.1 , 0.1 ) * vec3(c);


    fragColor = vec4(col,1.0);
}
