#define N_ITERS 100.0
#define PI 3.14159265

vec3 color(float t) {    
	vec3 a = vec3(0.5);
    vec3 b = vec3(0.5);
    vec3 c = vec3(1.0);
    vec3 d = vec3(0.0, 0.33, 0.67);
    return a + b * cos(2.0*PI*(c*t + d));
}

vec2 cmul(vec2 c1, vec2 c2) {
    return vec2(c1.x*c2.x - c1.y*c2.y, c1.x*c2.y + c1.y*c2.x);
}

float linscale(float x, float x1, float x2, float y1, float y2) {
	return (y2-y1)/(x2-x1) * (x-x1) + y1;
}

float shade(vec2 z, float B) {
    float t = iTime;
    float i;
    float K = linscale(sin(iTime), -1.0, 1.0, 0.1, 1.0);
    for (i=0.0; i<N_ITERS; i++) {
        // z[n+1] = z[n]^2 + K*e^(it)
        z = cmul(z,z) + K*vec2(cos(t),sin(t));
        if (length(z) > B) break;
    }
	float sn = i - log(log(length(z))/log(B))/log(2.0);
    return sn;
}

vec2 screen_coord(vec2 xy, vec2 dim) {
    return (xy - 0.5*dim) / min(dim.x, dim.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = 2.0*screen_coord(fragCoord.xy, iResolution.xy);
    float B = 256.0;
    vec3 col = color(shade(uv, B));
    fragColor = vec4(col,1.0);
}
