const highp vec2 center = vec2(0.2799803,0.010018);


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int iterations = int(200.0 * (iTime + 5.0)/20.0);

    highp float zoom = 1.0/(pow(2.0,iTime - 2.0));
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = (center) + zoom * vec2(iResolution.x/iResolution.y,1) *
              ((fragCoord - 0.5*iResolution.xy)/iResolution.xy);

    vec2 real_uv = fragCoord/iResolution.xy;
    vec2 iter = uv;
    vec2 c = vec2(0,0);
    int i = 0;
    for (;i < iterations; ++i){
        vec2 cpy = iter;
        iter.x = (cpy.x*cpy.x - cpy.y*cpy.y) + c.x;
        iter.y = 2.0*cpy.x*cpy.y + c.y;

        if (iter.x*iter.x + iter.y*iter.y >= 4.0){
            break;
        }
    }
    vec3 col;
    // Output to screen
    if (i != iterations) {
    	col = 0.66*(0.5*-cos(6.28*float(i)/17.0) + 1.0)*
              vec3(0.5*-cos(6.28*float(i)/16.0) + 1.0,
                   0.5*-sin(6.28*float(i)/19.0) + 1.0,
                   0.5*cos(6.28*float(i)/13.0) + 1.0
                  );
    } else {
        col = 0.5*vec3(0.6*real_uv.x + 0.14*(sin(iTime)+1.0),
                   0.6*real_uv.y + 0.14*(cos(iTime)+1.0),
                   0.6*real_uv.y*real_uv.x + 0.14*(sin(iTime)*sin(iTime)+1.0));
    }

    fragColor = vec4(col,1);
}
