float noise(float p){
	float fl = floor(p);
  	float fc = fract(p);
	return mix(rand(fl), rand(fl + 1.0), fc);
}

float noise(vec2 n) {
	const vec2 d = vec2(0.0, 1.0);
  	vec2 b = floor(n), f = smoothstep(vec2(0.0), vec2(1.0), fract(n));
	return mix(mix(rand(b), rand(b + d.yx), f.x), mix(rand(b + d.xy), rand(b + d.yy), f.x), f.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord)/iResolution.xy;
    uv-=.5;
    uv*=10.;

    float sunfac =clamp( (1. - length((uv- 3.*vec2(-cos(iTime),sin(iTime))) *vec2(1.7,1.)))*3. , 0., 1.);

    uv.x+=iTime;

    float sky = mix(1.,0.,uv.y/10.+0.5);
    vec3 skycol = vec3(sky*1.5,sky*1.5,1);
    skycol+=mix(0.,noise(uv*3.),noise(uv*0.1)*0.1);


    float ground = clamp((1.-sky/2.)*1.3, 0.,1.);
    vec3 groundcol = vec3(0,ground,0);
    vec3 mountaincol = vec3(ground);

    float rand = fract(sin(1924.231)*29183.);
    float height = (((sin(uv.x+2.23*rand)*1.2+sin(uv.x+1.23*2.12*rand)/rand+sin(uv.x*4.)/3.)/3.)+5.)/2.;
    float mountain_height = ((sin(uv.x*rand)*1.2+sin(uv.x*2.12142908*rand)/rand+sin(uv.x*4.)/3.)/3.)+5.-noise(uv.x*1.5)-noise(uv.x*5.)/5.;

    float groundfac = clamp( (uv.y+height)*20. , 0., 1.);
    float mountainfac = clamp( (uv.y-5.+mountain_height)*20. , 0., 1.);
    vec3 col;

    col = mix(skycol,vec3(1.,1.,0.),sunfac);
    col = mix(mountaincol,col,mountainfac);
    col = mix(groundcol,col,groundfac);


    fragColor = vec4(col,1.0);
}
