// Exact distance to a parabola, by solving a cubic equation.

// List of some other 2D distances: https://www.shadertoy.com/playlist/MXdSRf
//
// and https://iquilezles.org/articles/distfunctions2d


float sdParabola( in vec2 pos, in float k )
{
    pos.x = abs(pos.x);

    float ik = 1.0/k;
    float p = ik*(pos.y - 0.5*ik)/3.0;
    float q = 0.25*ik*ik*pos.x;

    float h = q*q - p*p*p;
    float r = sqrt(abs(h));

    float x = (h>0.0) ? 
        // 1 root
        pow(q+r,1.0/3.0) + pow(abs(q-r),1.0/3.0)*sign(p) :
        // 3 roots
        2.0*cos(atan(r,q)/3.0)*sqrt(p);

    float d = length(pos-vec2(x,k*x*x));

    return (pos.x<x)? -d : d;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;

    // animate
    float t = iTime/2.0;
    float px =  0.0 + 0.4*cos(t*1.1+5.5); // x position
    float py = -0.4 + 0.2*cos(t*1.2+3.0); // y position
    float pk =  8.0 + 7.5*cos(t*1.3+3.5); // width

    // sdf
    float d = sdParabola( p-vec2(px,py), pk );

    // colorize
    vec3 col = (d>0.0) ? vec3(0.9,0.6,0.3) : vec3(0.65,0.85,1.0);
	col *= 1.0 - exp(-6.0*abs(d));
	col *= 0.7 + 0.2*cos(110.0*d);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.015,abs(d)) );

    if( iMouse.z>0.001 )
    {
    d = sdParabola(m-vec2(px,py), pk );
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, abs(length(p-m)-abs(d))-0.0025));
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, length(p-m)-0.015));
    }

	fragColor = vec4(col,1.0);
}
