#define PI 3.14159265359
float T(float s){
    return sin(iTime*s)/2.+.5;
}
//----------------------------------------------------
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    float sum = sqrt(uv.x*uv.x + uv.y*uv.y)*2.;
    float sum2 = (atan(uv.y,uv.x)/(2.*PI))*(-1.);
    //uv = vec2(sum2,sum);
    uv = mix(vec2(sum,sum2),vec2(sum2,sum),sin(iTime));

    vec3[] colors = vec3[] (vec3(1.,0.,0.),vec3(0.,1.,0.),vec3(0.,0.,1.));
    float h = float(colors.length());


    uv.y += iTime;
    uv = fract(uv*1.);
    float smoo = mix(0.,1.,T(2.));
    int l = int(floor(uv.y*h));
    int g = int(mix(float(l+1),0. , float(l == int(h)-1)));

    vec3 col = mix(colors[l],colors[g],fract(smoo*uv*h).y);
    //col = vec3(fract(uv*h).y);

    fragColor = vec4(col,1.0);
}
