////////////////////////////////////////////////////////////////////////////////

mat2 r(float a){ return mat2(cos(a),sin(a),-sin(a),cos(a));}

vec2 N(float angle) {return vec2(sin(angle),cos(angle));}
#define tempo iTime/2.

float line(vec2 uv,float offset) {

    float p  = abs(uv.y)*2.;

    return smoothstep(.003,.001,length( (uv+vec2(0.,fract(offset+ 2.*iTime*.25)*.5)) - vec2(clamp(uv.x,-0.5*p,0.5*p),0.)));
}

float frame(vec2 uv) {
    vec2 uuv = uv ;
    uuv.y = -uuv.y;
   return 
   max(
        max(
            max(
                smoothstep(.003,.001,length( (uv  - clamp(uv.x,-0.5,0.5)))),
                smoothstep(.003,.001,length( (uuv - clamp(uv.x,-0.5,0.5))))
            ),
            max(
                smoothstep(.003,.001,length( (uv - vec2(clamp(uv.x,-0.5,0.5),uv.x*.5)))),
                smoothstep(.003,.001,length( (uuv - vec2(clamp(uv.x,-0.5,0.5),uv.x*.5))))
            )),
         max(
            smoothstep(.003,.001,length( (uv - vec2(clamp(uv.x,-0.5,0.5),uv.x*.2)))),
            smoothstep(.003,.001,length( (uuv - vec2(clamp(uv.x,-0.5,0.5),uv.x*.2))))
        )
);

}
float redline(vec2 uv) {
    uv.y*=mod(iTime,10.),
    uv= abs(uv);
    return smoothstep(.02,.010,length( (uv  - vec2(clamp(uv.x,-1.5,1.5),.0+.1))));

}
float unit(vec2 uv,float offset) {

   return max(line(-uv.yx,offset),max(line(uv.yx,offset),line(uv,offset)));
}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

   vec3 col =  vec3(0);
   float d = max(unit(uv,0.75),max(unit(uv,0.25),max(unit(uv,0.),unit(uv,0.5))));
   d += frame(uv);
col.r = redline(uv.yx);
   col.rg += d;


    fragColor = vec4( 
        col,
        1.0);
}
