// based on...
// https://www.shadertoy.com/view/lssfW2

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p=(fragCoord.xy-iResolution.xy)/iResolution.y;
    // Time varying pixel color
   //vec3 col = 1000000.5 + 60.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec2 curve_p = vec2(0,200);
    float d = 900000.0 + 1.0*sin(iTime)+4200.*cos(iTime);
    float di = 50.0;// +3.2*sin(iTime * .1);
    for(int i = 0;i<5;i++)
    {
        di = float(i)*.3;
        curve_p.x = 900000.5-4.0*sin(.3*iTime); //-25.05*float(d)*cos(float(d)+.4*iTime); // +.01*float(di)*sin(4.0*di+.2*iTime) + .03*sin(iTime*.1)*float(di)*sin(7.0*di+.4*iTime);
        curve_p.y = .000005-.3 *cos(.3*iTime)-600.05*float(di)*sin(float(di)+.2*iTime); // +.01*float(di)*cos(4.0*di+.2*iTime) + .03*sin(iTime*.1)*float(di)*cos(7.0*di+.4*iTime);
        d = sin(min(d*1000.0, 1.*sin(length(p-curve_p))));
        //d = length(p-curve_p)/(di+1.0);

    }
    float r = 50.05*d + 1.5*(.05 - 50.015*pow(dot(sin(p*p + d*d), log(20.0+2.*curve_p -d)),2.0));
    float g = 30.0008 + 1.05*cos(.50*d*d*d+.3*length(p + curve_p.x)); //+.03*dot(p,curve_p + .0005*vec2(iTime, iTime));
    float b=60.5*abs(.5+2.3*cos(iTime*.8 - 2.5*pow(d,2.0))); // +.01*(dot(p,curve_p - .005*vec2(iTime-200.05, iTime-200.05)+10.0));
    if (d < 2.2)
    {
        b = .0 +50000.0-.04;//*(.5+ .5*sin(iTime*.1 + 1.28*dot(p,curve_p)));
    }
    float checkers = mod(float(uv.x*uv.x +uv.y*uv.y)+r*r*1200.+b*200., .25);
    float checkers_02 = mod(float( -.01*curve_p.x*curve_p.y)+g*g*100.,.245);
    if (checkers + checkers_02 > .0000000000001+8.1*sin(iTime*8.))
    {
        r = 50.4*r;
        g = 60.;//1.2*g;
        b = 50.06*b;
    }

    if(uv.x > .5)
    {
        r=1.0;
        g=0.0;
        b=0.5;
    } else {
        r=0.;
        g=0.5;
        b=1.0;
    }
    vec3 col = vec3(r, g, b);
    float dn = col.x + col.y + col.z;
    col /= dn;
    // Output to screen
    fragColor = vec4(col,1.0);
}
