Main {
    vec4 a = A(U), b = B(U);
    vec4
        n = A(U+vec2(0,1)),
        e = A(U+vec2(1,0)),
        s = A(U-vec2(0,1)),
        w = A(U-vec2(1,0));
    vec3 no = normalize(vec3(e.w-w.w,n.w-s.w,.1));
    vec3 ref = reflect(vec3(0,0,1),no);
   	vec3 l = 2.*R.xyy;
    float li = ln(vec3(U,0),vec3(U,0)+ref,l);
    Q = (.2+a.w)*(0.7+0.3*sin(1.9+(abs(b.x*b.z-b.w*b.y)+abs(b.y*b.z-b.w*b.x))*3.+vec4(1,2,3,4)));
    Q *= exp(-.5*li)+10.*exp(-6.*li);
}

#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
#define C(U) texture(iChannel2,(U)/R)
#define D(U) texture(iChannel3,(U)/R)
#define Main void mainImage(out vec4 Q, in vec2 U)
float ln (vec2 p, vec2 a, vec2 b) { // returns distance to line segment for mouse input
    return length(p-a-(b-a)*clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.));
}
float ln (vec3 p, vec3 a, vec3 b) { // returns distance to line segment for mouse input
    return length(p-a-(b-a)*dot(p-a,b-a)/dot(b-a,b-a));
}

Main
{
	Q = A(U);vec2 f = vec2(0); vec4 b = B(U);float m = 0.;
    float s = 0.;
    for (int x = -1;x <= 1; x++)
    for (int y = -1;y <= 1; y++)
    if (x!=0||y!=0)
    {
    	vec2 u = vec2(x,y);
        vec4 bb = B(U+u);
        vec4 aa = A(U+u);
        u /= dot(u,u);
        vec2 w = 0.125*Q.w*aa.w*(mat2(bb)*mat2(bb)-mat2(1))*u;
        s += length(w);
        f += w;
        f += .1*0.125*Q.w*aa.w*(aa.xy-Q.xy);
    	m += 0.125*aa.w;
    }
    s/=m*Q.w;
    Q.y -= 1e-3;
    if (s>0.15) Q.w *= 0.9;
    Q.xy += f*Q.w;
    if (U.x<2.) Q.xy*=0.;//Q = vec4(0,0,0,1);
    if (R.x-U.x<2.) Q.xy*=0.;//Q = vec4(0,0,0,1);
    if (U.y<2.) Q.xy*=0.;//Q = vec4(0,0,0,1);
    if (R.y-U.y<2.) Q.xy*=0.;//Q = vec4(0,0,0,1);

    if (iMouse.z>0.)Q.x += .06*exp(-.3*length(U-iMouse.xy));
    if (iFrame<1) Q = vec4(0,0,0,1);
    Q = clamp(Q,-1.,1.);
}

