	Q = B(U);vec4 f = vec4(0), a = A(U), m = vec4(0);
    for (int x = -1;x <= 1; x++)
    for (int y = -1;y <= 1; y++)
    if (x!=0||y!=0)
    {
    	vec2 u = vec2(x,y);
        vec4 bb = B(U+u);
        vec4 aa = A(U+u);
        u /= dot(u,u);
        vec2 dv = aa.xy-a.xy;
        m += bb;
        f += 0.125*a.w*aa.w*vec4(u.x*dv.x,u.x*dv.y,u.y*dv.x,u.y*dv.y);
    }
    Q += vec4(mat2(Q)*mat2(f));
    Q = mix(Q,0.125*m,.001);
    if (iFrame<1) Q = vec4(1,0,0,1);
    Q = clamp(Q,-2.,2.);
}

