float hash(vec2 p) {

    p = fract(p*vec2(234.34, 435.345));
    p += sin(dot(p, p+342.23));
    return fract(p.x*p.y);


}
mat2 r(float a){return mat2(cos(a),sin(a),-sin(a),cos(a));}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
     vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
     uv*=r(sin(uv.x))*10.;

     vec2 guv = fract(uv+vec2(0,iTime));
     vec2 id = floor(uv+vec2(0,iTime));
     float d = guv.x;
     if(hash(id+iTime/1000000.) >0.3) d = 1.-guv.x;
     vec3 col = mix(vec3(0.1),vec3(.9,.9,.0),vec3(d));
     col.r *= .5+hash(id+iTime/1000000.) ;
     col.g *= .5+hash(id+iTime/1000000.) ;
     if(mod(id.y,  5.) == 0. ) col.b+=.9 ;
     else col.b=.0;

     if(mod(abs(id.x),5.) == 2.-step(0.,sign(id.x))) col*=vec3(1.9,1.2,.0);
    fragColor = vec4(col,1.0);
}
