// Cole Peterson
#define R iResolution
#define m vec2(R.x/R.y*(iMouse.x/R.x-.5),iMouse.y/R.y-.5)
#define ss(a, b, t) smoothstep(a, b, t)
mat2 rot(float a) {
    return mat2(cos(a), -sin(a), sin(a), cos(a));
}

void mainImage( out vec4 f, in vec2 u )
{
    vec3 col = vec3(0);

    float r = .47;
    float n = 24.;
    float sw = 0.0;

    for(float i = n; i > 0.; i--)
    {
        vec2 uv = vec2(u.xy - 0.5*R.xy)/R.y;
        uv *= (i*.001 + .1);
        uv *= rot(-i*.01);
        uv -= vec2(iTime*.012*sw, -iTime*.01);
        if(iMouse.z>0.)uv-=m*.15;
        vec2 id = floor(uv*8.);

        uv.y += sin(i*.5 + iTime*4. + uv.x*300.)*.002;
        uv.x += cos(i*.5 + iTime*4. + uv.y*300.)*.002;

        vec2 ruv = fract(uv*12.)-.5;
        id = floor(uv*12.);
        sw = floor(mod(id.y, 2.))*2. - 1.;

        vec3 nc = .55+.3*cos(vec3(2.,3.,0.4)*(id.x*40.+id.y+i*50. + iTime*.05)*3.);

        float s = max(pow(dot(ruv, .8*vec2(cos(iTime*1.85*sw), sin(iTime*1.85*sw)))*4.6, 5.0), 0.0001);
        nc *= s+.7;
        nc *= ((n-i)*1.4 / n);

    	col = mix(col, nc, ss(r, r - .015, length(ruv)));
        col *= ss(0.003, 0.01, abs(length(ruv) - r+.005));

        r -= i*0.001;
    }
    col = 1.-exp(-col*1.4);
    f = vec4(col,1.0);
}
