#define M_PI 3.1415926535897932384626433832795
vec2 rotate(vec2 v, float a) {
	float s = sin(a);
	float c = cos(a);
	mat2 m = mat2(c, -s, s, c);
	return m * v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv = rotate(uv,cos(iTime/5.0)*M_PI/4.0);
    uv -= vec2(iTime/5.0,cos(iTime)/10.0);
   	uv = fract(uv * 5.0);
	uv = mix(vec2(-0.5,-1.0),vec2(2.0,1.0),uv);

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyy+vec3(4,2,0));



    // aux vars
    float sq2 = 2.0 * sqrt(2.0);
    float x2 = pow(uv.x,2.0);
    float y2 = pow(uv.y,2.0);
    // fish equation (2*x^2+y^2)^2 -2sq(2) x(2x^2-3y^2)+2(y^2-x^2)
    float value = pow(2.0*x2+y2,2.0) 
        - sq2 * uv.x * (2.0*x2-3.0 * y2)
        +2.0 * (y2-x2);


    bool inCurve = value < 0.0;

    vec3 lightSea = vec3(0, 0.412, 0.58);
    vec3 darkSea = vec3(0.075, 0.333, 0.537);
    vec3 seaColor = mix(lightSea,darkSea,cos((fragCoord.x)/20.0));

    // Output to screen
    if(inCurve)
    	fragColor = vec4(col,1.0);
    else
        fragColor = vec4(seaColor,1.0);
}
