// inspired by https://en.wikipedia.org/wiki/Julia_set#/media/File:JSr07885.gif
// https://www.shadertoy.com/view/XljGzV
vec3 hsl2rgb( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );

    return c.z + c.y * (rgb-0.5)*(1.0-abs(2.0*c.z-1.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (y: -1 to 1 ;; x: -something to something)
    vec2 uv = (2.*fragCoord - iResolution.xy)/iResolution.y;

    vec2 z = uv;

    float my_const = 0.7885;
    float theta = iTime*0.4;
    vec2 c = vec2(my_const*cos(theta), my_const*sin(theta));

    const float max_steps = 65.;
    float boundary = 20.;
    float num_steps;

    for(num_steps = 0.; num_steps<max_steps; num_steps++) {
    	z = vec2(z.x*z.x - z.y*z.y, 2.*z.x*z.y) + c;
        if(length(z) > boundary) break;
    }

    float pixel_intensity = num_steps/max_steps;

    vec3 col = hsl2rgb(vec3(pixel_intensity + .6,1., smoothstep(1.,.8,pixel_intensity)*.5));

    // Output to screen
    fragColor = vec4(col, 1.0);
}
