#define FIELD 50.0

vec2 wavy(vec3 p, float t, float c) {
    return vec2(p.y*c* 0.01 * sin(t/4.)+22.);
}

vec2 sevenEQ(vec3 p, float t, float c){
	vec2 fx = p.xy * c;
	fx.x *= (fx.y + length(p*fx.x) - 0.3 * tan(t/2. + fx.y + p.y));
	fx.x *= abs(sin(p.x)) * 154. * fx.x;
	return fx;
}

vec2 sevenEQ2(vec3 p, float t, float c){
	vec2 fx = p.xy * c;
	fx.x *= (fx.y + length(p*fx.x) - 0.3 * sin(t/7. + fx.y + p.y));
	fx.x *= abs(sin(p.x)) * 154. * fx.x;
	return fx;
}


vec3 computeColor(vec2 fx, float t){
    vec3 tone = vec3(abs(sin(t/2.-15.)),abs(sin(t/13.+ 5.)),abs(cos(t/5. - 3.)));
	vec3 color = vec3(tone+0.988*fx.x);
	color -= (1.02 * fx.x);
	color.rb += color.g * 0.1;
	return clamp(color,(0.0),(1.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	float time = iTime / 4.;
	float ratio = iResolution.y/iResolution.x;
	fragCoord.y *= ratio;
	vec2 position = ( fragCoord.xy / iResolution.xy )-vec2(0.5,0.5*ratio);
	vec3 p = position.xyx*FIELD;
	p.z = 2.0*FIELD*0.5;
	vec3 color = vec3(0.,0.,0.);
    float fft = texture( iChannel0, vec2(0.1,0.1) ).x * 12.;

	p.z = 0.;
    float tmod = cos(sin(time + 0.15*fft) * 2.) + 3. * cos(fft);
  	color += computeColor(sevenEQ(p*0.5+vec3(1.*sin(tmod)*fft,tan(tmod/2.) * 2.5,0.), time, fft), time * 2.);
    color += computeColor(sevenEQ(p*0.5+vec3(1.*sin(tmod)*fft,sin(tmod/2.) * 1.5,0.), time, fft), time * 1.5);
    color += computeColor(sevenEQ(p*0.5+vec3(1.*tmod,-0.5,0.), time, fft), time * 1.25);
	color += computeColor(sevenEQ(p*0.5+vec3(0.,-1.5,0.), time, fft), time);
    color += computeColor(sevenEQ(p*0.5+vec3(-1.*tmod,-0.5,0.), time, fft), time * 1.25);
    color += computeColor(sevenEQ(p*0.5+vec3(-1.*sin(tmod)*fft,sin(tmod/2.) * 1.5,0.), time, fft), time * 1.5);
    color += computeColor(sevenEQ(p*0.5+vec3(-1.*sin(tmod)*fft, tan(tmod/2.) * 2.5,0.),time, fft), time * 2.);
    color += computeColor(wavy(p, time, fft), time);

    //color += computeColor(sevenEQ(p*0.05+vec3(0.,sin(tmod)*fft,0.), time, fft), time) * 0.1;
    color += computeColor(sevenEQ2(p*0.05+vec3(0.,sin(tmod)*fft,0.), time / 22., 3. * fft), time/11.) * 0.1 * fft;
	fragColor = vec4( color, 1.0 );

}
