float dist ( vec2 U, vec4 b )
{
   vec2 u = U-b.xy;
   u *= mat2(cos(-b.w),-sin(-b.w),sin(-b.w),cos(-b.w));
   // Change distance function here
    float a = 36.4*b.z;
    u = abs(u)-.25*b.z;
    u *= mat2(cos(a),-sin(a),sin(a),cos(a));

   u = abs(u)-b.z*vec2(.3,1); // use b.z to vary the shape
   return max(u.x,u.y);
}
