Main { Q = B(U); }

#define R iResolution.xy
#define A(U) texelFetch(iChannel0,ivec2(U),0)
#define B(U) texture(iChannel1,(U)/R)
#define C(U) texelFetch(iChannel2,ivec2(U),0)
#define D(U) texelFetch(iChannel3,ivec2(U),0)
#define pi 3.14159265359
#define phi 1.61803398875
#define Main void mainImage(out vec4 Q, vec2 U) 
#define e(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define N 12.
#define I 12.
vec2 hash22(vec2 p) // Dave H
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}
float sg (vec2 p, vec2 a, vec2 b) {
    return  length(p-a-(b-a)*clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.));

}

void Y (inout vec4 Q, vec2 U, vec4 n) {
	if ((length(U-Q.xy)>=length(U-n.xy)||length(Q.xy)<3.)&&
        length(n.xy)>3.) Q = n;
}
Main {
    U = round(U);
	Q = A(U);
    Q = round(Q);
    float iN = mod(float(iFrame),N);
    if (iN<.5) {
        if (mod(float(iFrame),N*I)<.5) {
           Q = vec4(R.x*(0.5),
                    0.4*R.y,
                    R.x*(0.5),
                    -R.x*.1);
        } else {
            vec4 t = Q;
            float n = floor(U.x+U.y*R.x);
            float a = mod(mod(float(iFrame),41.),6.)+1.;
            float s = 1.;//(1.+mod(n,a))-ceil(0.5*a);
            float b = 1.;
            float l = (mod(n,2.));
            float an = iTime*.2*sign(l*2.-1.)*phi*pi;
            t.xy = Q.xy-.7*(Q.zw-Q.xy)*e(an);
        	t.zw = Q.xy;
            if (iN<1.) Q = t;
            else Y(Q,U,t);
        }
    }
    else {
        float k = exp2(N-1.-mod(float(iFrame),N));
    	Y(Q,U,A(U+vec2(0,k)));
    	Y(Q,U,A(U+vec2(k,0)));
    	Y(Q,U,A(U-vec2(0,k)));
    	Y(Q,U,A(U-vec2(k,0)));
    }
}

Main {
    vec4 b = B(U);
    float iN = mod(float(iFrame),N);
    float IN = mod(float(iFrame)/N,I);
    if (iN>N-2.&&IN>1.) {
        vec4 a = A(U);
        float l = length(a.xy-a.zw);
        if (length(a.xy)<3.||length(a.zw)<3.){Q = B(U); return;}
        float r = length(U-a.xy);
        Q = (0.7+0.5*sin(
            2.*sin(.1*float(iFrame))+
            .01*float(iFrame)+
            (4.+mod(float(iFrame),N*I))/N/I*(vec4(1,2,3,4))));
        float p = (.1*l);
        float x = (smoothstep(.3*p,.2*p,abs(r-2.5*p))*0.5+smoothstep(3.*p,2.*p,r))/(1.+.05*l);
        Q  = mix(b,Q,0.5*x);
    } else {
         vec4 m = 0.25*(B(U+vec2(0,1))+B(U+vec2(1,0))+B(U-vec2(0,1))+B(U-vec2(1,0)));
        Q = b = mix(b,m*0.99,.05);
    }
}
