mat2 rotate(float a){
    return mat2(cos(a),sin(a),-sin(a),cos(a));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){

    vec2 uv = (fragCoord.xy -0.5* iResolution.xy)/iResolution.x;
    vec2 p = uv;
    //rotate ring
    p*=rotate(iTime/1.);

    //create ring
    float ang = 6.283185/90.0;
    float sector = round(atan(p.x,p.y)/ang);
    float an = sector*ang;
    vec2 q = p;
    q = rotate(an)*q;

    //duplicate ring radialy 
    float c = 0.006;
    q.y-=iTime/10.;
    q.y = mod(q.y+0.5*c,c)-0.5*c;

    //make circles
    vec3 r = vec3(length(q));
    vec3 color = smoothstep(0.0019,0.002,r);
    fragColor = vec4(color,1.0);
}
