vec3 pal(in float i) {
    const float twopi = 2.0 * 3.1415926;
    vec4 p = vec4(3.0, 2.0, 2.0, 3.0);
    vec4 ph = vec4(0.1, 0.2, 0.3, 0.25);
    vec4 col = cos(twopi * (i * p + ph));
    return (col.rgb * 0.4 + 0.6) * abs(col.a);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec4 b = texture(iChannel0, uv);
    vec3 col = pal(length(b.xz));
    fragColor = vec4(col,1.0);
}

const vec2 maxres = vec2(640.0,360.0);
const vec2 centre = vec2(0.35, 0.35);
const vec4 fade = vec4(0.001);

float random(in float x) {
    x = log2(abs(x) + 0.000001);
    x = fract(sin(x) * 21.56789 + cos(x) * 11.98765);
    return fract(x + 2.0);
}

float random(in float x, in float y) {
    return random(sin(x * 7.1010101) + cos(y * 9.3131313) + 2.1);
}

vec2 fuzz(in vec2 seed) {
    return vec2(random(seed.x, iTime), random(seed.y, iTime));
}

vec2 fuzz() {
    return fuzz(vec2(123.,456.));
}

vec4 mouseplot(in vec4 col, in vec2 uv) {
    vec2 m = iMouse.xy / iResolution.xy;
    vec2 a = vec2(sin(iTime * 0.3), cos(iTime * 0.25))
           * vec2(cos(iTime * 0.2501), -sin(iTime * 0.11111))
           * 0.35 + 0.5;
    if (m.x <= 0.05) m = a;
    m += 0.03 * fuzz();

    float d = length(uv - m);
    return mix(vec4(1.0, 0.0, 1.0, 0.0), col, smoothstep(0.005,0.01, d));
}

vec2 f(in vec2 uv) {
    const float th = 0.01;
    const float r = 0.995;
    float c = cos(th), s = sin(th);
    uv = (uv - centre) * mat2(c, s, -s, c) * r + centre;

    return uv;
}

vec2 q(in vec2 uv, in vec2 res, in vec2 seed) {
    uv *= res;
#if 1
    uv += fuzz(seed);
#elif 1
    uv += fuzz(seed).yx;
#elif 1
    vec2 sd = uv;
    sd = vec2(sd.x + sd.y, sd.x - sd.y);
    uv += fuzz(sd);
#else
    vec2 sd = fuzz(seed);
    sd = vec2(sd.x + sd.y, sd.x - sd.y);
    uv += sd;
#endif
    return floor(uv) / res;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iChannelResolution[0].xy;
    vec2 resolution = min(iChannelResolution[0].xy, maxres);
    vec2 seed = floor(uv * resolution + vec2(0.5));
    vec2 uv2 = uv;
    uv = f(uv);
    uv2 = vec2(1.,1.) - f(vec2(1.,1.) - uv2);
    uv = q(uv, resolution, seed);
    uv2 = q(uv2, resolution, seed);
    vec4 feedback = texture(iChannel0, uv);
    vec4 shadow = texture(iChannel0, uv2);
    vec4 col = feedback;
    col.zw = shadow.zw;

    mat4 col_twist = mat4( .99,.01,.00,.00,
                           .00,.99,.01,.00,
                           .00,.00,.99,.01,
                           .01,.00,.00,.99);
    //col *= col_twist;
	// TODO: some kind of dithered blend/twist between components,
    // not a smooth thing like this, which ruins the pixelley effect.
    col = max(vec4(0.0), col - fade);


    fragColor = mouseplot(col, uv);
}
