#define psin(x) (.5+.5*sin(x))
#define xor(a, b) (a+b-2.*a*b)

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec2 R = iResolution.xy;
    vec2 uv = (fragCoord - R*.5) / R.y;
    float s = 15.;
    uv *= s;
    vec2 gv = fract(uv) - .5;
    vec2 id = floor(uv) + .5;
    vec3 col = vec3(0);

    float t = iTime;
    float m = 0., n = 0.;
    for (int i = 0; i < 25; ++i) {
        vec2 o = vec2(i%5, i/5)-2.;
        float l = length(id - o) - t;
        float r = psin(l * .4)
            + .5 * psin(l * .294)
            + .3 * psin(l * .87)
            + .2;
        n = xor(n, smoothstep(r, r - 2.*s/R.y, length(gv + o)));
        m += smoothstep(r, r - 2.*s/R.y, length(gv + o));
        //m = length(uv);
    }
    col += hsv2rgb(vec3(fract(m*0.05), 1., n));

    fragColor = vec4(col,1.0);
}
