#define PI 3.1415926
#define TAU 6.2831852

#define SF 1./min(iResolution.x,iResolution.y)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy*.5)/iResolution.y;

    float a = atan(uv.y, uv.x)/TAU;

    float l = length(uv);
    float m = 0.;
    for(float n=0.; n<6.; n+=1.){
        float i = n*PI*.25;
        float t = iTime*1. + i;

        float tt = 16.;
        float f = t + a*PI*tt;

        float s = sin(f)*.5+.5;
        float w = sin(PI*iTime + f + a*PI*4.)*.5+.5;

        float bs = w*.01;

    	m = max(m, smoothstep(SF*2.+bs, bs, abs(l - (s*.25 + .225))) * (w*.5+.5) );
    }


    fragColor = vec4(m);
}
