float sdCircle( vec2 p, float r )
{
  return length(p) - r;
}

float rand(float x)
{
    return fract(sin(x) * 43758.5453);
}

float triangle(float x)
{
	return abs(1.0 - mod(abs(x), 2.0)) * 2.0 - 1.0;
}

float randomNoise(float x, float y)
{
    return fract(sin(dot(vec2(x, y), vec2(12.9898, 78.233))) * 43758.5453);
}

float _Indensity = 0.7; 


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = floor(iTime * 16.0) / 16.0;
	vec2 uv = fragCoord.xy / iResolution.xy;


    vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    p += vec2(triangle(uv.y * rand(time * 0.1) * 1.0) * rand(time * 2.1) * 0.004,
			triangle(uv.x * rand(time * 0.1) * 1.0) * rand(time * 3.1) * 0.004);

    float x = (p.x + 4.0 ) * (p.y + 4.0 ) * (iTime);
	float grain = fract(sin(dot(p.xy, vec2(17.0,180.)))* 2400. + iTime);

	float d = sdCircle( p, -0.1 );

    float c = iTime * 0.8;
    vec3 col = vec3(d) - vec3(0.5);
	col *= 11. * cos(35. * d - d * pow(d, .1)) + 11.* d * cos(abs(c*1.3) + 0.8) * 11.; 

    // Grain
	grain = 1.0 - grain;
    vec3 color = mix(col, vec3(grain), 0.3);


    // Depth alpha
    float alpha = col.x * grain*0.007; // cine shader depth
	fragColor = vec4(color,alpha);
}

