#define T smoothstep(0.,1.,fract(iTime*.5))

mat2 rot(float a){float s=sin(a),c=cos(a);return mat2(c,-s,s,c);}

float sdPlane(vec3 p) {
   float plane = abs(p.z)-.001;
   plane = max(plane, p.x);
   plane = max(plane, p.y);
   plane = max(plane, -p.x-p.y-1.);
   return plane;
}

float dist(vec3 p){
   p.z -= 2.;
   //p=abs(p);
   //p=mod(p,2.);
   p.xy -= .3+texture(iChannel0,vec2(.9,.25)).r*.6-.1;
   float p0, p1,p2;

   p.xy+=(.5*T);
   p.xy*=rot(3.1415*5./4.*T);
   p.xy*=mix(1.,sqrt(2.),T);

   p1 = sdPlane(p);
   p.xz*=rot(-3.1415*T);
   p2 = sdPlane(p);
   return min(p1,p2)/2.*.2;
}

vec3 norm(vec3 p){
    vec2 e = vec2(.01,0.);
	return normalize(vec3(
    	dist(p+e.xyy)-dist(p-e.xyy),
    	dist(p+e.yxy)-dist(p-e.yxy),
        dist(p+e.yyx)-dist(p-e.yyx)
    ));
}

void mainImage( out vec4 fragColor, in vec2 FC )
{
    float d=0., e, i;
    vec2 uv = (FC*2.-iResolution.xy)/iResolution.y;
    vec3 p,rd=normalize(vec3(uv,2.));
    for(i=0.;i++<99.;){
        p = d*rd;
        //p.xz*=rot(iTime);
        d+=e=dist(p);
        if(e<.001)break;
    }
    if(d>1000.)return;
    //fragColor = vec4(vec3(dot(norm(p),vec3(-1.))*.5+.5),1.);
    fragColor = vec4(vec3(norm(p).zyx*.3+.7),1.);
}
