float t;
const float pi=acos(-1.0);
float hlane(vec2 uv, float a) { const float aa=0.0025; float a2=a*0.5; return smoothstep(-a2-aa,-a2+aa,uv.y)-smoothstep(a2-aa,a2+aa,uv.y); }
vec3 flag(vec2 uv) {
	const vec3 r=vec3(0.8275,0.1255,0.0666);
	const vec3 y=vec3(0.988,0.784,0.0);
	vec3 c=vec3(0.0);
	uv.y-=0.2920;c+=hlane(uv,0.045)*r;
	uv.y+=0.0955;c+=hlane(uv,0.146)*y;
	uv.y+=0.0955;c+=hlane(uv,0.045)*r;
	uv.y+=0.1900;c+=hlane(uv,0.045)*r;
	return c;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy-0.5;
    t=mod(iTime, 20.0*pi);
	float motion=sin(sin(t*0.75)+t*0.445);
	motion*=motion*motion;
	uv *= 1.0+0.25*uv.x*motion*motion;
	uv.x-=0.02+0.04*motion;
	vec3 col=vec3(0.0);
	float decal=(0.9+0.1*sin(uv.x*3.0-(t*2.0)*2.0))*sin(1.5*(sin(t+2.0*sin(t*0.25))-3.0*sin(4.0*uv.x+5.0*uv.y)+uv.x+uv.y)-3.0*t);
	uv *= 1.0-0.04*decal;
	{
		vec2 uv = uv, tuv = uv;
        uv.y += max(0.,dot(tuv,normalize(vec2(1.0,-0.070*pi)))+0.15)*1.6;
        uv.y += max(0.,dot(tuv,normalize(vec2(1.0,-0.035*pi)))-0.07)*-2.4;
        col = flag(uv);
	}
	col+=0.1*decal+pow(abs(decal), 10.0)*(uv.x+0.1*sin(t+sin(t*2.0)))*0.8;
    fragColor = vec4(col,1.0);
}
