void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy*2.-1.;
    float aspect = iResolution.y / iResolution.x;
    vec3 v=vec3(0.,0.,1.)+vec3(uv.x,0.,0.)+vec3(0.,uv.y*aspect,0.);
    vec3 o=vec3(0.,0.,-2.);
    float r=0.5;

    float a=dot(v,v);
    float b=2.*dot(v,o);
    float c=dot(o,o)-r*r;
    float d=b*b-4.*a*c;

    fragColor = vec4(0.);
    if(d>=0.)
    {
        float t=(-b-sqrt(d))/(2.*a);
        vec3 p=o+v*t;
        vec3 n=normalize(p);

        vec2 bumpUV = vec2(atan(n.z,n.x), acos(n.y));
        vec4 bump = texture(iChannel0, bumpUV);

        // Warning: Has fireflies on derivative discontinuities.

        // How do our UVs vary?
        float du0 = dFdx(bumpUV.x);
        float dv0 = dFdx(bumpUV.y);
        float du1 = dFdy(bumpUV.x);
        float dv1 = dFdy(bumpUV.y);

        // How does our position vary in the same direction?
        vec3 e0=dFdx(p);
        vec3 e1=dFdy(p);

        // What is our bump gradient?
        float pdx=dFdx(bump.r);
        float pdy=-dFdy(bump.r);

        vec3 tang=(e1*dv0-e0*dv1)/max(dv0*du1-dv1*du0,0.0001f);
        vec3 bitang=cross(n,tang);

        n=normalize(cross(tang+n*pdx,bitang+n*pdy));

        float rot=iTime*0.5;
        vec3 l=normalize(vec3(cos(rot),0.707,-sin(rot)));

        fragColor=vec4(1.)*dot(n,l);
    }  
}
