#define bpm iTime*(138./60.*8.)
#define sbpm mod(floor(bpm) + pow(fract(bpm),.005),8.)
////////////////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////////////////
vec3 fig(vec2 uv,float p,vec3 ttt) {
    float d = length(uv);
    vec3 col = vec3(.1/pow(d,p));
    return mix(vec3(.1),vec3(.4,.3-length(uv*.2),.3-length(uv*.3)),col)*ttt ;

}
mat2 r(float a){
float c= cos(a),s=sin(a);
return mat2(c,-s,s,c);
}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{

    vec2 uv = (fragCoord.xy-.5* iResolution.xy)/iResolution.y;
    //uv*=r(sbpm/8.);
       uv*=r( (uv.x* max(uv.y*4.,0.))*.2);
    vec2 gv = fract(uv*2.-.5)-.5;
    vec2 id = floor(uv);
    vec3 col = vec3(0.);

    for(float y=-1.0;y<=1.0;y++){
    for(float x=-1.0;x<=1.0;x++){
    vec2 offset= vec2(x,y+(2.-sbpm)*.1);
        float m  =mod(sbpm+(sin(id.x*20.))  ,8.) > 2. ? 2.*length(uv) : 0.;
        vec2 z = mod(sbpm+(sin(id.x*20.))  ,8.) <= 2. ? vec2(1.,fract(y)) : vec2(1.);


        vec3 b = fig( fract(gv+offset.yx*r(cos(iTime)))-.5,1.005,vec3(1.,.5,0.2));

        vec3 coll = 
            b +
            fig( (gv*z+offset),2.,vec3(.1,.5,1.))+
            fig(uv+vec2(sin(iTime*1.6)*.5,cos(iTime*4.)*.4),0.9 +m ,vec3(.2,.5,1.)) +
            fig(uv+vec2(cos(iTime*3.33)*.3,sin(iTime*1.6)*.4),.9+m,vec3(1.,.5,0.2)) ;;
        col += coll/9.;
    }
    }
    fragColor = vec4(col,1.0);
}
