vec2 c = vec2(7.0,5.0); //change this constant to get different patterns!
//vec2 c = vec2(7.0,9.0);
//vec2 c = vec2(2.0,2.5); //looks like a maze


vec2 triangle_wave(vec2 a,float scale){
    return abs(fract((a+c)*scale)-.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col;  
    float t1 = 4.0;
    float offset = .16;
    float scale2 = 1.05;
    vec2 uv = (fragCoord-iResolution.xy)/iResolution.y/t1/2.0;
    uv += vec2(iTime/2.0,iTime/3.0)/t1/8.0;
    for(int c=0;c<3;c++){
        float scale = 1.4;



        for(int i=0;i<6;i++)
        {

            uv = triangle_wave(uv+offset,scale)+triangle_wave(uv.yx,scale);
            uv = triangle_wave(uv+col.xy,scale);
            //uv.y *= -1.0;
            scale /= scale2+col.x;
            offset /= scale2;

        }
     col[c] = fract((uv.x)-(uv.y));
	}

    fragColor = vec4(vec3(col),1.0);

}
