vec3 flag(vec2 uv) {
    if(uv.y < 0.25) {
     	return vec3(1.0, 0.96, 0.04);
    } else if(uv.y < 0.5) {
        return vec3(1.0);
    } else if(uv.y < 0.75) {
     	return vec3(0.61, 0.35, 0.83);   
    } else {
        return vec3(0.0);
    }
}

vec2 warp(vec2 uv) {
    vec2 uv1 = uv + vec2(
    	uv.y*0.1,
        0.0
    );
    vec2 d = vec2(
        0.0,
        0.23*sin(iTime + round(0.017*(uv1.x * 2.0 - 1.0)*4000.0))
    );
	return uv + d;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	uv.y = 1.0 - uv.y;
    uv = warp(uv);

    vec3 c0 = flag(uv);

    // Output to screen
    fragColor = vec4(c0, 1.0);
}
