// Left,   arithmetic blending (regular blending)
// Middle, geometric blending
// Right,  harmonic blemnding
//
// Geometric and harmonic blending are to regular
// blending as the geometric and harmonic means are
// to the arithmetic mean. You can also see it like
// blending in logarithmic and inverse space.
//
// Geometric and harmonnic blending are gamma agnostic.
//
// A(a,b,t) = a(1-t)+bt
// G(a,b,t) = a^(1-t)*b^t
// H(a,b,t) = a*b/[b(1-t)+at]
//
// min <= H <= G <= A <= max

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord/iResolution.xy;

    float f = fract(3.0*p.x);
    vec2 uv = vec2(f/3.0,p.y);

    vec3 colA = texture(iChannel0,uv).xyz;
    vec3 colB = texture(iChannel1,uv).xyz;

    if( p.y>0.85 )
    {
        colA = vec3(1.0,0.8,0.2);
        colB = vec3(0.2,0.8,1.0);
    }
    else
    {
        f = 0.5+0.49*sin(6.283185*iTime/3.0);
    }

    const float kG = 2.2;
    colA = pow(colA,vec3(kG));
    colB = pow(colB,vec3(kG));

    vec3 col = vec3(0.0);
    // arithmetic blending
    if( p.x<0.333 ) 
       col = colA*(1.0-f) + colB*f;
    // geometric blending
    else if( p.x<0.666 ) 
       col = pow(colA,vec3(1.0-f)) * pow(colB,vec3(f));
    // harmonic blending       
    else 
       col = colA*colB/(colB*(1.0-f)+colA*f);

    col *= smoothstep( 0.002, 0.005, abs(p.x-0.333) );
    col *= smoothstep( 0.002, 0.005, abs(p.x-0.666) );
    col *= smoothstep( 0.002, 0.006, abs(p.y-0.85) );

    fragColor = vec4(pow(col,vec3(1.0/kG)),1.0);
}
