const float min_scale = 10.0;
const float max_scale = 200.0;

vec2 random2( vec2 p ) {
    return fract(sin(vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))))*43758.5453);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float scale = min_scale + (max_scale - min_scale) * (0.5 + sin(iTime * 0.677) * 0.5);
    vec2 st = fragCoord / iResolution.xy;
    st.x *= iResolution.x / iResolution.y;

    st *= scale;

    vec2 i_st = floor(st);
    vec2 f_st = fract(st);
    float m_dist = 100.;  // minimum distance
    vec2 uv = st;
    for(int y = -1; y <= 1; ++y){
        for(int x = -1; x <= 1; ++x){
        	vec2 neighbor = vec2(float(x),float(y));
            vec2 point = random2(i_st + neighbor);
            point = 0.5 + 0.5*sin(iTime + 6.2831*point);
            vec2 diff = neighbor + point - f_st;
            float dist = length(diff);

            // Keep the closer distance
            if(dist < m_dist){
            	m_dist = dist;
                uv = st + diff;
            }
        }
    }


    uv /= scale;
    uv.x /= iResolution.x / iResolution.y;
    vec3 color = texture(iChannel0, uv).rgb;
    //color += m_dist;

    //center
    //color += 1.-step(.06, m_dist);

    //color.r +=  step(0.98, f_st.x) + step(0.98, f_st.y);

    fragColor = vec4(color, 1.0);
}
