#define PI 3.14159

#define iTime0 (iTime - 18.59)

float Dip(float time, float range){
    return smoothstep(time-range,time,iTime) - smoothstep(time,time+range,iTime);
}

float Cross(in vec2 a, in vec2 b){
    return a.x*b.y-a.y*b.x;
}

vec3 Pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d ){
    return a + b*cos( 6.28318*(c*t+d) );
}

const mat2 m2 = mat2(
    cos(PI/4.), -sin(PI/4.),
    sin(PI/4.), cos(PI/4.)
);

float sdLine(in vec2 a, in vec2 b, in vec2 p){
    vec2 ba = b-a;
    vec2 pa = p-a;
    vec2 c = a + normalize(ba)*clamp(dot(pa, normalize(ba)), 0.0, length(ba));
    vec2 pc = p-c;
    return 	abs(dot(pc,normalize(m2*ba))) +
        	abs(dot(pc,normalize(ba*m2)));
}

float sdLineShadow(in vec2 a, in vec2 b, in vec2 p, in vec2 o){
    vec2 oa = o-a;
    vec2 ob = o-b;
    vec2 op = o-p;
    float s = sign(Cross(ob,oa));
    vec2 ba = normalize(b-a);
    vec2 pa = p-a;
    vec2 c = a + ba*dot(pa, ba);
    vec2 pc = p-c;
    return (clamp(-s*pc.y, 0.0, 1.0))
        	* sign(min(s*Cross(ob, op), s*Cross(op, oa)));

}

vec3 DrawLine(in vec2 a, in vec2 b, in vec2 p, in vec2 o){
    float d = sdLine(a, b, p);
    float l = sdLineShadow(a, b, p, o);

    vec3 col = vec3(0.0);

    col += 0.02 * clamp(sign(l), 0.0, 1.0)
        * Pal(l*2.+iTime0*5., vec3(0.5,0.5,0.5),vec3(0.5,0.5,0.5),vec3(1.0,1.0,1.0),vec3(0.0,0.33,0.67) );

    col += smoothstep(0.015, 0.01, d);

    return col;
}

vec3 DrawLetterM(vec2 p, vec2 o, in float s, in vec2 of){
    return vec3(0.0)
        + DrawLine((vec2(-1., 1.)+of)*s, (vec2(1., 1.)+of)*s, p, o)
        + DrawLine((vec2(-1., -1.)+of)*s, (vec2(1., -1.)+of)*s, p, o)
        + DrawLine(m2*(vec2(-1., -1.)+of)*s, m2*(vec2(-1., 1.)+of)*s, m2*p, o)
        + DrawLine(m2*(vec2(1., -1.)+of)*s, m2*(vec2(1., 1.)+of)*s, m2*p, o)
    ;
}
vec3 DrawLetterI(vec2 p, vec2 o, in float s, in vec2 of){
    return vec3(0.0)
        + DrawLine(m2*(vec2(0., -1.4)+of)*s, m2*(vec2(0., 1.2)+of)*s, m2*p, o)
    ;
}
vec3 DrawLetterN(vec2 p, vec2 o, in float s, in vec2 of){
    return vec3(0.0)
        + DrawLine((vec2(-1., -1.)+of)*s, (vec2(1., -1.)+of)*s, p, o)
        + DrawLine(m2*(vec2(-1., -1.)+of)*s, m2*(vec2(-1., 1.)+of)*s, m2*p, o)
    ;
}
vec3 DrawLetterA(vec2 p, vec2 o, in float s, in vec2 of){
    return vec3(0.0)
        + DrawLine(m2*(vec2(0., -1.4)+of)*s, m2*(vec2(0., 1.2)+of)*s, m2*p, o)
        + DrawLine(m2*(vec2(0., -0.1)+of)*s, m2*(vec2(0.5, -0.1)+of)*s, m2*p, o)
    ;
}

vec3 DrawMina(in vec2 p, in vec2 o, in vec2 of){
    return vec3(0.0)
        + DrawLetterM(p, o, 0.5, vec2(-1.9, 0.5)-of)
        + DrawLetterI(p, o, 0.5, vec2(-0.6, 0.5)-of)
        + DrawLetterN(p, o, 0.5, vec2(1.0, 0.5)-of)
        + DrawLetterA(p, o, 0.5, vec2(2.3, 0.5)-of)
	;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uvs = fragCoord/iResolution.xy;
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;


    vec3 col = vec3(0.0);

    float f = 1.0
        - Dip(1.0, 0.25)
        - Dip(4.0, 0.25)
	;


    vec2 p = uv*(2.5+0.5*sin(iTime0));
    vec2 o = vec2(0.);

    col += DrawMina(p, o, sin(iTime0*vec2(1.71, 1.37))*3.);

    vec3 lastFrame = texture(iChannel0, uvs).rgb;

    fragColor = vec4(pow(col, vec3(0.4545)) + lastFrame*(0.8+0.1*sin(iTime0*2.)),1.0);
}
