#define strip(v) (   n!=1 ? mod(floor(v),2.)                                 \
                   :        smoothstep(-.8/s,.8/s, abs(mod(v+1.,2.)-1.)-.5 ) \
                 )                    
void mainImage( out vec4 O, vec2 u )
{
    vec2 R  = iResolution.xy,
         U  = u ;
    float s = .2+floor(U.y/R.y*5.);              // strip width
    int   n = int(u.x/R.x*3.);                   // subwindow index

    O-=O;
    if ( int(U)*3%int(R)<3 || int(U.y)*5%int(R.y)<5) return; // black separators
    U /= s;                                      // scale strips

    if (n<2) {
        O.ra += strip(U.x);                      // red strip

        U *= mat2(cos( .05 + vec4(0,11,33,0) )); // rotate second layer
        O.ga += (1.-O.a) *strip(U.x);            // blend green strip behind red layer
    } 
    else {                                       // --- same with numerical integration
        float d=1./100., x,i,a;
        for ( i=-.5;  i<.5; i+=d) {              // oversampling
            x = U.x+i/s;
            a = strip(x); O.ra += a;             // red strip
         // O.ra += a = strip(x);                // no longer compiles
            x = dot( vec2(x,U.y),  cos( .05 + vec2(0,11) )); // rotate
            O.ga += (1.-a) *strip(x);            // subpixel-blend green strip behind red layer
        }
        O *= d;
    }

    O = pow(O,vec4(1./2.2));                     // to sRGB
}
