#define COUNT 3.
#define SF 1./min(iResolution.x,iResolution.y)
#define SS(l,s) smoothstep(SF,-SF,l-s)

#define PI 3.1415926
#define PI2 6.2831852

#define hue(h) clamp( abs( fract(h + vec4(3,2,1,0)/3.) * 6. - 3.) -1. , 0., 1.)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 uv = fragCoord/iResolution.y;

    uv *=5.;
    float idx = floor(uv.x);
    float idy = floor(uv.y);
    uv = fract(uv);  
    uv.y -= .5;


    float m=0.;
    vec3 col;
    for(float i=0.; i<COUNT; i+= 1.){
        float ic = fract(i/COUNT + idx/COUNT + idy/COUNT);
        float oi = ic * PI2;
        float edge = sin(uv.x * 3.14 * 2. + oi)*.25;
        float mi = SS(edge, uv.y) - SS(edge + .2, uv.y);
        m += mi;

        if(mi > 0.){
            col = hue(ic).rgb;
        }
    }

    col = col * m;

    fragColor = vec4(col,1.0);
}
