#define RED_COL vec3(170, 0, 0)/255.

vec2 rand( vec2 p ) {
    return fract(sin(vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))))*43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec2 uv = (fragCoord - iResolution.xy*.5)/iResolution.y;        

    uv *= 10.;

    vec2 iuv = floor(uv);
    vec2 guv = fract(uv);

    float mDist = 1.0;

    for (float y= -1.; y <= 1.; y++) {
        for (float x= -1.; x <= 1.; x++) {            
            vec2 neighbor = vec2(x, y);            
            vec2 point = rand(iuv + neighbor);			
            point = 0.5 + 0.5*sin(iTime + 6.2831*point);		
            vec2 diff = neighbor + point - guv;            
            float dist = length(diff) * (1.0 + rand(iuv + neighbor + 100.).x*.25);

            mDist = min(mDist, dist);
        }
    }

    mDist = .1/(1.0 - clamp(mDist, 0.0, 1.));       

    vec3 color = mix(RED_COL, vec3(0.), mDist) * 1.5;        
    color *= smoothstep(0.0, 1.0, color.r);    

    fragColor = vec4(color,1.0);    
}
